words=[{text:"dangerous",translation:"מסוכן",partOfSpeech:"adjective",sentences:[{from:"Walking alone at night can be dangerous.",to:"ללכת לבד בלילה יכול להיות מסוכן."},{from:"It is dangerous to drive without a seatbelt.",to:"זה מסוכן לנהוג ללא חגורת בטיחות."},{from:"The broken glass is dangerous.",to:"הזכוכית השבורה מסוכנת."}]},{text:"prefer",translation:"להעדיף",partOfSpeech:"verb",sentences:[{from:"I prefer tea over coffee.",to:"אני מעדיף תה על פני קפה."},{from:"She prefers reading at night.",to:"היא מעדיפה לקרוא בלילה."},{from:"Do you prefer summer or winter?",to:"אתה מעדיף קיץ או חורף?"}]},{text:"feed",translation:"להאכיל",partOfSpeech:"verb",sentences:[{from:"Please feed the dog twice a day.",to:"אנא האכל את הכלב פעמיים ביום."},{from:"She feeds the birds in the park.",to:"היא מאכילה את הציפורים בפארק."},{from:"It's important to feed children healthy food.",to:"חשוב להאכיל ילדים במזון בריא."}]},{text:"list",translation:"רשימה",partOfSpeech:"noun",sentences:[{from:"I have a long shopping list.",to:"יש לי רשימת קניות ארוכה."},{from:"Please check the list again.",to:"בדוק שוב את הרשימה."},{from:"He wrote a list of tasks for the day.",to:"הוא כתב רשימת משימות ליום."}]},{text:"usual",translation:"רגיל",partOfSpeech:"adjective",sentences:[{from:"She arrived at her usual time.",to:"היא הגיעה בשעה הרגילה שלה."},{from:"It's just the usual routine.",to:"זה פשוט השגרה הרגילה."},{from:"We went to our usual place for dinner.",to:"הלכנו למקום הרגיל שלנו לארוחת ערב."}]},{text:"serve",translation:"להגיש",partOfSpeech:"verb",sentences:[{from:"They serve breakfast all day.",to:"הם מגישים ארוחת בוקר כל היום."},{from:"Can you serve the guests now?",to:"תוכל להגיש לאורחים עכשיו?"},{from:"The restaurant serves Italian food.",to:"המסעדה מגישה אוכל איטלקי."}]},{text:"team",translation:"קבוצה/צוות",partOfSpeech:"noun",sentences:[{from:"Our team won the game.",to:"הקבוצה שלנו ניצחה במשחק."},{from:"He is part of a professional team.",to:"הוא חלק מקבוצה מקצועית."},{from:"The team worked together on the project.",to:"הצוות עבד יחד על הפרויקט."}]},{text:"unusual",translation:"לא רגיל",partOfSpeech:"adjective",sentences:[{from:"That is an unusual idea.",to:"זו רעיון לא רגיל."},{from:"He has an unusual way of thinking.",to:"יש לו דרך מחשבה לא רגילה."},{from:"It was an unusual sight.",to:"זה היה מראה לא רגיל."}]},{text:"fix",translation:"לתקן",partOfSpeech:"verb",sentences:[{from:"Can you fix this broken chair?",to:"תוכל לתקן את הכיסא השבור הזה?"},{from:"He knows how to fix cars.",to:"הוא יודע איך לתקן מכוניות."},{from:"I need to fix my computer.",to:"אני צריך לתקן את המחשב שלי."}]},{text:"variety",translation:"מגוון",partOfSpeech:"noun",sentences:[{from:"This store has a great variety of products.",to:"בחנות זו יש מגוון רחב של מוצרים."},{from:"We need more variety in our diet.",to:"אנחנו זקוקים למגוון רחב יותר בתזונה שלנו."},{from:"The festival offered a variety of music styles.",to:"הפסטיבל הציע מגוון של סגנונות מוזיקליים."}]},{text:"careful",translation:"זהיר",partOfSpeech:"adjective",sentences:[{from:"Be careful with the glass.",to:"היה זהיר עם הזכוכית."},{from:"She is careful when she crosses the street.",to:"היא זהירה כשהיא חוצה את הרחוב."},{from:"You must be careful when handling chemicals.",to:"עליך להיות זהיר בעת טיפול בכימיקלים."}]},{text:"follow",translation:"לעקוב",partOfSpeech:"verb",sentences:[{from:"Please follow the instructions.",to:"אנא עקוב אחרי ההוראות."},{from:"Follow me to the meeting room.",to:"עקוב אחרי לחדר הישיבות."},{from:"The cat likes to follow her around the house.",to:"החתול אוהב לעקוב אחריה ברחבי הבית."}]},{text:"river",translation:"נהר",partOfSpeech:"noun",sentences:[{from:"The river flows into the sea.",to:"הנהר זורם לתוך הים."},{from:"We went fishing in the river.",to:"יצאנו לדוג בנהר."},{from:"The city is located near a large river.",to:"העיר ממוקמת ליד נהר גדול."}]}];