words=[{text:"oil",translation:"שמן",partOfSpeech:"noun",sentences:[{from:"We need more oil for cooking.",to:"אנחנו זקוקים ליותר שמן לבישול."},{from:"The car's engine needs oil.",to:"מנוע הרכב זקוק לשמן."},{from:"Olive oil is healthy.",to:"שמן זית בריא."}]},{text:"possible",translation:"אפשרי",partOfSpeech:"adjective",sentences:[{from:"Is it possible to finish this today?",to:"האם זה אפשרי לסיים את זה היום?"},{from:"She did all that was possible.",to:"היא עשתה את כל שהיה אפשרי."},{from:"Explore all possible options.",to:"חקור את כל האפשרויות הקיימות."}]},{text:"interested in",translation:"מתעניין ב...",partOfSpeech:"verb",sentences:[{from:"He is interested in learning Spanish.",to:"הוא מתעניין בלמידת ספרדית."},{from:"Are you interested in playing tennis?",to:"האם אתה מתעניין בשחק טניס?"},{from:"She's interested in art.",to:"היא מתעניינת באמנות."}]},{text:"village",translation:"כפר",partOfSpeech:"noun",sentences:[{from:"He lives in a small village.",to:"הוא גר בכפר קטן."},{from:"The village is quiet at night.",to:"הכפר שקט בלילה."},{from:"They visited a historic village.",to:"הם ביקרו בכפר היסטורי."}]},{text:"typical",translation:"טיפוסי/אופייני",partOfSpeech:"adjective",sentences:[{from:"It's a typical English village.",to:"זה כפר אנגלי טיפוסי."},{from:"That's typical of him to say.",to:"זה טיפוסי לו לומר."},{from:"She reacted in a typical way.",to:"היא הגיבה בצורה אופיינית."}]},{text:"travel",translation:"לטייל",partOfSpeech:"verb",sentences:[{from:"They love to travel around the world.",to:"הם אוהבים לטייל ברחבי העולם."},{from:"Travel broadens the mind.",to:"טיולים מרחיבים את האופקים."},{from:"Can we travel next summer?",to:"האם נוכל לטייל בקיץ הבא?"}]},{text:"choice",translation:"בחירה",partOfSpeech:"noun",sentences:[{from:"You have a choice to make.",to:"יש לך בחירה לעשות."},{from:"It was a difficult choice.",to:"זו הייתה בחירה קשה."},{from:"She made her choice clear.",to:"היא הבהירה את בחירתה."}]},{text:"wide",translation:"רחב",partOfSpeech:"adjective",sentences:[{from:"The river is very wide here.",to:"הנהר רחב מאוד כאן."},{from:"He opened his eyes wide.",to:"הוא פתח את עיניו רחב."},{from:"They offer a wide range of services.",to:"הם מציעים מגוון רחב של שירותים."}]},{text:"imagine",translation:"לדמיין",partOfSpeech:"verb",sentences:[{from:"Imagine a world without war.",to:"דמיין עולם ללא מלחמות."},{from:"Can you imagine flying to Mars?",to:"תוכל לדמיין טיסה למאדים?"},{from:"I can't imagine life without music.",to:"אני לא יכול לדמיין חיים בלי מוזיקה."}]},{text:"lovely",translation:"נעים, נחמד",partOfSpeech:"adjective",sentences:[{from:"What a lovely day it is today!",to:"איזה יום נחמד זה היום!"},{from:"She has a lovely smile.",to:"יש לה חיוך נעים."},{from:"They live in a lovely neighborhood.",to:"הם גרים בשכונה נעימה."}]},{text:"join",translation:"להצטרף",partOfSpeech:"verb",sentences:[{from:"Can I join you for dinner?",to:"האם אפשר להצטרף אליכם לארוחת ערב?"},{from:"She decided to join the club.",to:"היא החליטה להצטרף למועדון."},{from:"Join us for a walk.",to:"הצטרף אלינו לטיול."}]}];