words=[{text:"suggest",translation:"להציע",partOfSpeech:"verb",sentences:[{from:"I suggest we meet at the library to study together.",to:"אני מציע שנפגש בספרייה כדי ללמוד יחד."},{from:"The teacher suggested reading this book for homework.",to:"המורה הציע לקרוא את הספר הזה לשיעורי בית."}]},{text:"control",translation:"שליטה",partOfSpeech:"noun",sentences:[{from:"The pilot had full control of the aircraft during the storm.",to:"לטייס הייתה שליטה מלאה במטוס במהלך הסערה."},{from:"Parents should maintain control over their children's screen time.",to:"הורים צריכים לשמור על שליטה בזמן המסך של ילדיהם."}]},{text:"impossible",translation:"בלתי אפשרי",partOfSpeech:"adjective",sentences:[{from:"It seemed impossible to climb the mountain, but they succeeded.",to:"נראה היה בלתי אפשרי לטפס על ההר, אבל הם הצליחו."},{from:"It's impossible to learn a language without practicing.",to:"בלתי אפשרי ללמוד שפה בלי להתאמן."}]},{text:"repeat",translation:"לחזור על",partOfSpeech:"verb",sentences:[{from:"Could you please repeat what you just said?",to:"האם תוכל בבקשה לחזור על מה שאמרת?"},{from:"The teacher asked the students to repeat the exercise.",to:"המורה ביקש מהתלמידים לחזור על התרגיל."}]},{text:"wonderful",translation:"נפלא",partOfSpeech:"adjective",sentences:[{from:"We had a wonderful time at the beach yesterday.",to:"היה לנו זמן נפלא בחוף אתמול."},{from:"The concert was absolutely wonderful!",to:"הקונצרט היה נפלא לחלוטין!"}]},{text:"solve",translation:"לפתור",partOfSpeech:"verb",sentences:[{from:"It took us an hour to solve the difficult math problem.",to:"לקח לנו שעה לפתור את בעיית המתמטיקה הקשה."},{from:"Scientists are trying to solve the mystery of the missing plane.",to:"מדענים מנסים לפתור את תעלומת המטוס הנעדר."}]},{text:"as quickly as possible",translation:"כמה שיותר מהר",partOfSpeech:"phrase",sentences:[{from:"Please finish your homework as quickly as possible.",to:"אנא סיים את שיעורי הבית שלך כמה שיותר מהר."},{from:"The doctor needs to see you as quickly as possible.",to:"הרופא צריך לראות אותך כמה שיותר מהר."}]},{text:"vacation",translation:"חופשה",partOfSpeech:"noun",sentences:[{from:"We are going on vacation to Italy next summer.",to:"אנחנו הולכים לחופשה באיטליה בקיץ הבא."},{from:"The school vacation starts next week.",to:"חופשת בית הספר מתחילה בשבוע הבא."}]},{text:"adventure",translation:"הרפתקה",partOfSpeech:"noun",sentences:[{from:"Our hiking trip through the mountains was quite an adventure.",to:"הטיול שלנו בהרים היה הרפתקה של ממש."},{from:"The children love reading adventure stories.",to:"הילדים אוהבים לקרוא סיפורי הרפתקאות."}]},{text:"advantage",translation:"יתרון",partOfSpeech:"noun",sentences:[{from:"Knowledge of multiple languages gives you an advantage in the job market.",to:"ידיעת מספר שפות נותנת לך יתרון בשוק העבודה."},{from:"Being tall is an advantage in basketball.",to:"להיות גבוה הוא יתרון בכדורסל."}]},{text:"definitely",translation:"בהחלט",partOfSpeech:"adverb",sentences:[{from:"I will definitely attend your birthday party on Saturday.",to:"אני בהחלט אגיע למסיבת יום ההולדת שלך בשבת."},{from:"This is definitely the best pizza in town.",to:"זה בהחלט הפיצה הטובה ביותר בעיר."}]},{text:"passenger",translation:"נוסע",partOfSpeech:"noun",sentences:[{from:"The flight attendant asked all passengers to fasten their seatbelts.",to:"דיילת האוויר ביקשה מכל הנוסעים לחגור את חגורות הבטיחות שלהם."},{from:"The bus driver counted the passengers before leaving.",to:"נהג האוטובוס ספר את הנוסעים לפני היציאה."}]},{text:"anytime",translation:"בכל עת",partOfSpeech:"adverb",sentences:[{from:"Feel free to call me anytime if you need help with your project.",to:"אל תהסס להתקשר אליי בכל עת אם אתה צריך עזרה עם הפרויקט שלך."},{from:"You can visit us anytime you want.",to:"אתה יכול לבקר אותנו בכל עת שתרצה."}]},{text:"anyone",translation:"כל אחד",partOfSpeech:"pronoun",sentences:[{from:"Does anyone know the answer to this question?",to:"האם מישהו יודע את התשובה לשאלה הזו?"},{from:"Anyone can learn to play the piano with practice.",to:"כל אחד יכול ללמוד לנגן בפסנתר עם תרגול."}]},{text:"anywhere",translation:"בכל מקום",partOfSpeech:"adverb",sentences:[{from:"I could not find my keys anywhere in the house.",to:"לא הצלחתי למצוא את המפתחות שלי בשום מקום בבית."},{from:"You can sit anywhere you want in the classroom.",to:"אתה יכול לשבת בכל מקום שתרצה בכיתה."}]}];