words=[{text:"discover",translation:"לגלות",partOfSpeech:"verb",sentences:[{from:"Scientists discovered a new species of fish in the deep ocean.",to:"מדענים גילו מין חדש של דג באוקיינוס העמוק."},{from:"I discovered that I really enjoy cooking.",to:"גיליתי שאני באמת נהנה לבשל."}]},{text:"experience",translation:"ניסיון/חוויה",partOfSpeech:"noun",sentences:[{from:"This was a wonderful experience that I'll never forget.",to:"זו הייתה חוויה נפלאה שלעולם לא אשכח."},{from:"She has a lot of experience in teaching.",to:"יש לה הרבה ניסיון בהוראה."}]},{text:"happen",translation:"לקרות",partOfSpeech:"verb",sentences:[{from:"What happened to your car?",to:"מה קרה למכונית שלך?"},{from:"Accidents can happen to anyone.",to:"תאונות יכולות לקרות לכל אחד."}]},{text:"imagine",translation:"לדמיין",partOfSpeech:"verb",sentences:[{from:"Can you imagine living on the moon?",to:"האם אתה יכול לדמיין חיים על הירח?"},{from:"I never imagined I would become a teacher.",to:"מעולם לא דמיינתי שאני אהפוך למורה."}]},{text:"instead",translation:"במקום",partOfSpeech:"adverb",sentences:[{from:"Instead of going to the beach, we went to the park.",to:"במקום ללכת לחוף, הלכנו לפארק."},{from:"I'll have tea instead of coffee.",to:"אני אשתה תה במקום קפה."}]},{text:"invent",translation:"להמציא",partOfSpeech:"verb",sentences:[{from:"Thomas Edison invented the light bulb.",to:"תומס אדיסון המציא את נורת החשמל."},{from:"Scientists are trying to invent new ways to save energy.",to:"מדענים מנסים להמציא דרכים חדשות לחסוך באנרגיה."}]},{text:"laugh",translation:"לצחוק",partOfSpeech:"verb",sentences:[{from:"The children laughed at the funny story.",to:"הילדים צחקו מהסיפור המצחיק."},{from:"It's good to laugh every day.",to:"טוב לצחוק בכל יום."}]},{text:"lose",translation:"לאבד",partOfSpeech:"verb",sentences:[{from:"I don't want to lose my keys again.",to:"אני לא רוצה לאבד את המפתחות שלי שוב."},{from:"The team didn't want to lose the game.",to:"הקבוצה לא רצתה להפסיד במשחק."}]},{text:"maybe",translation:"אולי",partOfSpeech:"adverb",sentences:[{from:"Maybe we should try a different approach.",to:"אולי כדאי שננסה גישה אחרת."},{from:"Maybe it will rain tomorrow.",to:"אולי ירד גשם מחר."}]},{text:"mean",translation:"להתכוון/משמעות",partOfSpeech:"verb/noun",sentences:[{from:"What do you mean by that?",to:"למה אתה מתכוון בזה?"},{from:"The word has several different meanings.",to:"למילה יש כמה משמעויות שונות."}]},{text:"meet",translation:"לפגוש",partOfSpeech:"verb",sentences:[{from:"I'll meet you at the library at 3 PM.",to:"אני אפגוש אותך בספרייה ב-3 אחר הצהריים."},{from:"Nice to meet you!",to:"נעים להכיר!"}]},{text:"message",translation:"הודעה",partOfSpeech:"noun",sentences:[{from:"I left a message on your voicemail.",to:"השארתי הודעה בדואר הקולי שלך."},{from:"Did you get my message?",to:"האם קיבלת את ההודעה שלי?"}]},{text:"mistake",translation:"טעות",partOfSpeech:"noun",sentences:[{from:"Everyone makes mistakes sometimes.",to:"כולם עושים טעויות לפעמים."},{from:"I found a mistake in my homework.",to:"מצאתי טעות בשיעורי הבית שלי."}]},{text:"must",translation:"חייב",partOfSpeech:"modal verb",sentences:[{from:"You must finish your homework before watching TV.",to:"אתה חייב לסיים את שיעורי הבית לפני הצפייה בטלוויזיה."},{from:"We must be at the airport by 8 AM.",to:"אנחנו חייבים להיות בשדה התעופה עד 8 בבוקר."}]},{text:"need",translation:"צריך",partOfSpeech:"verb",sentences:[{from:"I need help with my math homework.",to:"אני צריך עזרה עם שיעורי הבית במתמטיקה."},{from:"Do you need anything else?",to:"האם אתה צריך משהו נוסף?"}]},{text:"should",translation:"צריך/צריך היה",partOfSpeech:"modal verb",sentences:[{from:"You should study for the test.",to:"אתה צריך ללמוד למבחן."},{from:"I should have called you earlier.",to:"הייתי צריך להתקשר אליך מוקדם יותר."}]}];