words=[{text:"see",translation:"saw",partOfSpeech:"verb",sentences:[{from:"I see the moon.",to:"אני רואה את הירח."},{from:"She sees the problem clearly.",to:"היא רואה את הבעיה בבירור."}]},{text:"do",translation:"did",partOfSpeech:"verb",sentences:[{from:"We do our best.",to:"אנחנו עושים את המרב."},{from:"He does the work efficiently.",to:"הוא עושה את העבודה ביעילות."}]},{text:"have",translation:"had",partOfSpeech:"verb",sentences:[{from:"They have a good time.",to:"יש להם זמן טוב."},{from:"I have a question.",to:"יש לי שאלה."}]},{text:"am",translation:"was",partOfSpeech:"verb",sentences:[{from:"I am happy today.",to:"אני שמח היום."},{from:"Am I late?",to:"האם אני מאחר?"}]},{text:"is",translation:"was",partOfSpeech:"verb",sentences:[{from:"The weather is nice.",to:"המזג אוויר נחמד."},{from:"Is it time to go?",to:"האם הגיע הזמן ללכת?"}]},{text:"are",translation:"were",partOfSpeech:"verb",sentences:[{from:"They are here.",to:"הם כאן."},{from:"Are you coming?",to:"האם אתה בא?"}]},{text:"discover",translation:"discovered",partOfSpeech:"verb",sentences:[{from:"Scientists discovered a new species of fish in the deep ocean.",to:"מדענים גילו מין חדש של דג באוקיינוס העמוק."},{from:"I discovered that I really enjoy cooking.",to:"גיליתי שאני באמת נהנה לבשל."}]},{text:"go",translation:"went",partOfSpeech:"verb",sentences:[{from:"We went to the park yesterday.",to:"הלכנו לפארק אתמול."}]},{text:"eat",translation:"ate",partOfSpeech:"verb",sentences:[{from:"She ate an apple for breakfast.",to:"היא אכלה תפוח לארוחת בוקר."}]},{text:"write",translation:"wrote",partOfSpeech:"verb",sentences:[{from:"He wrote a letter to his friend.",to:"הוא כתב מכתב לחבר שלו."}]},{text:"drive",translation:"drove",partOfSpeech:"verb",sentences:[{from:"She drove to work yesterday.",to:"היא נהגה לעבודה אתמול."}]},{text:"become",translation:"became",partOfSpeech:"verb",sentences:[{from:"He became a doctor after many years of study.",to:"הוא הפך לרופא אחרי שנים רבות של לימודים."}]},{text:"fly",translation:"flew",partOfSpeech:"verb",sentences:[{from:"They flew to London last year.",to:"הם טסו ללונדון בשנה שעברה."}]},{text:"take",translation:"took",partOfSpeech:"verb",sentences:[{from:"He took a pen from the table.",to:"הוא לקח עט מהשולחן."}]},{text:"dream",translation:"dreamt",partOfSpeech:"verb",sentences:[{from:"He dreamt about a giant cake.",to:"הוא חלם על עוגה ענקית."}]},{text:"lose",translation:"lost",partOfSpeech:"verb",sentences:[{from:"I lost my keys yesterday.",to:"איבדתי את המפתחות שלי אתמול."}]},{text:"sit",translation:"sat",partOfSpeech:"verb",sentences:[{from:"She sat on the chair.",to:"היא ישבה על הכיסא."}]}];