words=[{text:"dimanche",translation:"ראשון",partOfSpeech:"noun",sentences:[{from:"Aujourd'hui c'est dimanche.",to:"היום יום ראשון."},{from:"Je me repose le dimanche.",to:"אני נח ביום ראשון."},{from:"Dimanche, je vais au parc.",to:"ביום ראשון, אני הולך לפארק."}]},{text:"lundi",translation:"שני",partOfSpeech:"noun",sentences:[{from:"Demain c'est lundi.",to:"מחר יום שני."},{from:"Je travaille le lundi.",to:"אני עובד ביום שני."},{from:"Lundi, j'ai cours.",to:"ביום שני, יש לי שיעור."}]},{text:"mardi",translation:"שלישי",partOfSpeech:"noun",sentences:[{from:"C'est mardi aujourd'hui.",to:"היום יום שלישי."},{from:"Le mardi, je vais à la piscine.",to:"ביום שלישי, אני הולך לבריכה."},{from:"J'ai un rendez-vous mardi.",to:"יש לי פגישה ביום שלישי."}]},{text:"mercredi",translation:"רביעי",partOfSpeech:"noun",sentences:[{from:"Mercredi, je vais chez le médecin.",to:"ביום רביעי, אני הולך לרופא."},{from:"C'est mercredi.",to:"זה יום רביעי."},{from:"Le mercredi, les enfants n'ont pas école.",to:"ביום רביעי, לילדים אין בית ספר."}]},{text:"jeudi",translation:"חמישי",partOfSpeech:"noun",sentences:[{from:"Jeudi, je mange avec mes amis.",to:"ביום חמישי, אני אוכל עם חברים שלי."},{from:"C'est jeudi.",to:"זה יום חמישי."},{from:"Le jeudi, j'étudie le français.",to:"ביום חמישי, אני לומד צרפתית."}]},{text:"vendredi",translation:"שישי",partOfSpeech:"noun",sentences:[{from:"Vendredi, c'est la fin de la semaine.",to:"יום שישי הוא סוף השבוע."},{from:"J'aime le vendredi.",to:"אני אוהב את יום שישי."},{from:"Vendredi, je fais les courses.",to:"ביום שישי, אני עושה קניות."}]},{text:"samedi",translation:"שבת",partOfSpeech:"noun",sentences:[{from:"Samedi, je reste à la maison.",to:"בשבת, אני נשאר בבית."},{from:"C'est samedi.",to:"זו שבת."},{from:"Le samedi, nous nous reposons.",to:"בשבת, אנחנו נחים."}]}];