words=[{text:"avec",translation:"עם",partOfSpeech:"preposition",sentences:[{from:"Je suis avec mon ami.",to:"אני עם החבר שלי."},{from:"Elle mange avec sa famille.",to:"היא אוכלת עם המשפחה שלה."},{from:"Il joue avec un ballon.",to:"הוא משחק עם כדור."}]},{text:"il y a",translation:"יש",partOfSpeech:"phrase",sentences:[{from:"Il y a un livre sur la table.",to:"יש ספר על השולחן."},{from:"Il y a trois chaises.",to:"יש שלושה כסאות."},{from:"Il y a du café.",to:"יש קפה."}]},{text:"un garçon",translation:"ילד",partOfSpeech:"noun",sentences:[{from:"C'est un garçon.",to:"זה ילד."},{from:"Le garçon joue.",to:"הילד משחק."},{from:"Je vois un garçon.",to:"אני רואה ילד."}]},{text:"une fille",translation:"ילדה",partOfSpeech:"noun",sentences:[{from:"C'est une fille.",to:"זאת ילדה."},{from:"La fille lit un livre.",to:"הילדה קוראת ספר."},{from:"Je vois une fille.",to:"אני רואה ילדה."}]},{text:"une voiture",translation:"מכונית",partOfSpeech:"noun",sentences:[{from:"C'est une voiture.",to:"זאת מכונית."},{from:"La voiture est rouge.",to:"המכונית אדומה."},{from:"Il a une voiture.",to:"יש לו מכונית."}]},{text:"sa maman",translation:"אמא שלו/שלה",partOfSpeech:"noun",sentences:[{from:"Sa maman est là.",to:"אמא שלו/שלה כאן."},{from:"Il parle avec sa maman.",to:"הוא מדבר עם אמא שלו."},{from:"Sa maman cuisine.",to:"אמא שלו/שלה מבשלת."}]},{text:"son papa",translation:"אבא שלו/שלה",partOfSpeech:"noun",sentences:[{from:"Son papa travaille.",to:"אבא שלו/שלה עובד."},{from:"Elle va avec son papa.",to:"היא הולכת עם אבא שלה."},{from:"Son papa lit un journal.",to:"אבא שלו/שלה קורא עיתון."}]},{text:"quand est tu né ?",translation:"מתי נולדת?",partOfSpeech:"phrase",sentences:[{from:"Quand est né ton frère?",to:"מתי נולד אחיך?"},{from:"Quand est né le bébé?",to:"מתי נולד התינוק?"},{from:"Quand est né David?",to:"מתי נולד דוד?"}]},{text:"je suis né le",translation:"נולדתי ב",partOfSpeech:"phrase",sentences:[{from:"Je suis né en 2000.",to:"נולדתי בשנת 2000."},{from:"Je suis née en mai.",to:"נולדתי במאי."},{from:"Je suis né à Paris.",to:"נולדתי בפריז."}]},{text:"quand est elle née...?",translation:"מתי היא נולדה...?",partOfSpeech:"phrase",sentences:[{from:"Quand est elle née, ta sœur?",to:"מתי היא נולדה, אחותך?"},{from:"Quand est elle née, Marie?",to:"מתי היא נולדה, מרי?"},{from:"Quand est elle née, la petite?",to:"מתי היא נולדה, הקטנה?"}]},{text:"elle est née le...",translation:"היא נולדה ב...",partOfSpeech:"phrase",sentences:[{from:"Elle est née le 10 mars.",to:"היא נולדה ב-10 במרץ."},{from:"Elle est née le lundi.",to:"היא נולדה ביום שני."},{from:"Elle est née le matin.",to:"היא נולדה בבוקר."}]},{text:"quand est il né...?",translation:"מתי הוא נולד...?",partOfSpeech:"phrase",sentences:[{from:"Quand est il né, ton frère?",to:"מתי הוא נולד, אחיך?"},{from:"Quand est il né, Pierre?",to:"מתי הוא נולד, פייר?"},{from:"Quand est il né, le bébé?",to:"מתי הוא נולד, התינוק?"}]},{text:"il est né le...",translation:"הוא נולד ב...",partOfSpeech:"phrase",sentences:[{from:"Il est né le 5 avril.",to:"הוא נולד ב-5 באפריל."},{from:"Il est né le vendredi.",to:"הוא נולד ביום שישי."},{from:"Il est né le soir.",to:"הוא נולד בערב."}]},{text:"nom",translation:"שם משפחה",partOfSpeech:"noun",sentences:[{from:"Mon nom est Dupont.",to:"שם המשפחה שלי הוא דופון."},{from:"Quel est ton nom?",to:"מה שם המשפחה שלך?"},{from:"Son nom est Martin.",to:"שם המשפחה שלו הוא מרטין."}]},{text:"prenom",translation:"שם פרטי",partOfSpeech:"noun",sentences:[{from:"Mon prénom est Pierre.",to:"השם הפרטי שלי הוא פייר."},{from:"Quel est ton prénom?",to:"מה השם הפרטי שלך?"},{from:"Son prénom est Marie.",to:"השם הפרטי שלה הוא מרי."}]}];